Player player = new Player();
    
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
        if (player.isGameOn)
            {
            player.moveForward();
            int p = player.p.getValue();
            //System.out.println("p int value " + p);

            String y=Integer.toString(player.p.getValue());
           
            ProgressBar.setValue(player.p.getValue()); 
            ProgressBar.setString(y);

            //String p2=Integer.toString(p);
            //PTextFeild.setText(p2);  //this was for troubleshooting

            if (player.h.getValue()>0){
                String w=Integer.toString(player.h.getValue());
                HealthText.setText(w);}
            else
            {
                HealthText.setText("Dead");
                p=0;
                String p3=Integer.toString(p);
                //PTextFeild.setText(p3); //was for troubleshooting

                LossDialogBox.setSize(300,300);
                LossDialogBox.setVisible(true);
                
            }
            
            if (player.p.getValue()>=100 && player.h.getValue()>0)
            {
                WinDialogBox.setSize(300,300);
                WinDialogBox.setVisible(true);
                
            }
        }
       
        else
        {
            LossDialogBox.setSize(300,300);
            LossDialogBox.setVisible(true);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

