    private void resetGame()
    {
        player.reset();
        //set 
        LossDialogBox.hide();

        ProgressBar.setValue(player.p.getValue()); 
        ProgressBar.setString(player.p.getValueInStr());

        HealthText.setText(player.h.getValueInStr());

        CoinsRemaining.setText(player.c.getValueInStr());
    }
