    private void GoToShopActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_GoToShopActionPerformed

        String w=Integer.toString(player.h.getValue());
        HealthText.setText(w);
        CurrentHealthShopText.setText(w);
        Shop.setSize(250,300);
        
        Shop.setVisible(true);
    }//GEN-LAST:event_GoToShopActionPerformed

    private void BuyGauzeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BuyGauzeActionPerformed

        if (player.c.getValue()>=3)
        {
            player.buyGauze();
            CoinsRemaining.setText(player.c.getValueInStr());
            HealthText.setText(player.h.getValueInStr());
            CurrentHealthShopText.setText(player.h.getValueInStr());
        }
        else
        {
            OutOfCoins.setSize(500,250);
            OutOfCoins.setVisible(true);
        }
    }//GEN-LAST:event_BuyGauzeActionPerformed

    private void BuyBandaidActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BuyBandaidActionPerformed

        if (player.c.getValue()>=2)
        {
            player.buyBandaid();
            
            CoinsRemaining.setText(player.c.getValueInStr());
            HealthText.setText(player.h.getValueInStr());
            CurrentHealthShopText.setText(player.h.getValueInStr());
        }
        else
        {
            OutOfCoins.setSize(500,250);
            OutOfCoins.setVisible(true);
        }
        
    }//GEN-LAST:event_BuyBandaidActionPerformed

